import EventItem from "../components/EventItem";
import EventsList from "../components/EventsList";
import { Await, redirect, useRouteLoaderData } from "react-router-dom";
import { Suspense } from "react";

export default function EventDetailPage() {
    const { event, events } = useRouteLoaderData('event-detail');

    return (
        <>
            <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
                <Await resolve={event}>
                    {(loadedEvent) => <EventItem event={loadedEvent} />}
                </Await>
            </Suspense>
            <Suspense fallback={<p style={{ textAlign: 'center' }}>Loading...</p>}>
                <Await resolve={events}>
                    {(loadedEvents) => <EventsList events={loadedEvents} />}
                </Await>
            </Suspense>
        </>
    );
}

const BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export async function loadEvents(id) {
    try {
        const response = await fetch(`${BASE_URL}/events/${id}`);
        if (!response.ok) {
            throw new Error('Could not fetch details for the selected event.');
        }
        const resData = await response.json();
        return resData.event;
    } catch (error) {
        console.error(error);
        throw new Response(
            JSON.stringify({ message: 'An unexpected error occurred.' }),
            { status: 500 }
        );
    }
}

export async function loadAllEvents() {
    try {
        const response = await fetch(`${BASE_URL}/events`);
        if (!response.ok) {
            throw new Error('Could not fetch the list of events.');
        }
        const resData = await response.json();
        return resData.events;
    } catch (error) {
        console.error(error);
        throw new Response(
            JSON.stringify({ message: 'An unexpected error occurred.' }),
            { status: 500 }
        );
    }
}

export async function loader({ params }) {
    const id = params.id;

    return {
        event: await loadEvents(id),
        events: await loadAllEvents()
    };
}

export async function action({ request, params }) {
    const id = params.id;

    if (request.method !== 'DELETE') {
        throw new Response(
            JSON.stringify({ message: 'Method not allowed.' }),
            { status: 405 }
        );
    }

    const response = await fetch(`${BASE_URL}/events/${id}`, {
        method: 'DELETE',
    });

    if (!response.ok) {
        throw new Response(
            JSON.stringify({ message: 'Could not delete the selected event.' }),
            { status: 500 }
        );
    }
    return redirect('/events');
}
